
public class loopLecture2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 0;
		while(count<10) {
			int x=10;
			System.out.print(count + " ");
			count++;
			x++;
			System.out.print("{"+x+"}");
			// destroy and remain

		}
		System.out.println();
		System.out.println(count);
		//System.out.print(x);

	}

}
